import React from 'react';
import './style.css';
import API from '../../utils/API'

function ResultsCard(props) {

    // componentWillMount() { 
    //     axios.get("https://www.googleapis.com/books/v1/volumes?q=coraline")
    //         .then((res) => {
    //             this.setState(
    //                 {
    //                     title: res.data.items[0].volumeInfo.title,
    //                     authors: res.data.items[0].volumeInfo.authors[0],
    //                     description: res.data.items[0].volumeInfo.description,
    //                     image: res.data.items[0].volumeInfo.imageLinks.smallThumbnail,
    //                     link: res.data.items[0].volumeInfo.infoLink,
    //                 }
    //             );
    //         })
    // }


    return (
        // <div className="container">
        <div className="row">
            <div id="results" className="col s12 m6">
                <div className="card">
                    <div className="card-content">
                        <span className="card-title">Results</span>
                        <br />
                        {
                            props.books.map((book) => {
                                return(
                                    <ul className="collection">
                                        <li className="collection-item">
                                            <div className="row">
                                                <h5 className="title">{book.title}</h5>
                                            </div>
                                            <div className="row">
                                                <span>Written By: {book.authors}</span>
                                            </div>
                                            <div className="row center-align">
                                                <img className="col" src={book.image} alt="" />
                                                <p className="col s10">{book.description}</p>
                                            </div>
                                        </li>
                                    </ul>
                                );
                            })
                        }
                    </div>
                </div>
            </div>
        </div>
        // </div>
    )
}

export default ResultsCard;
