import React from 'react';
import './style.css';

class SearchCard extends React.Component {

    state = {
        searchIn: ""
    }

    handleChange(event) {
        this.setState({searchIn: event.target.value});
    }

    render() {
        return (
            // <div className="container">
            <div className="row">
                <div id="search" className="col s12 m6">
                    <div className="card">
                        <div className="card-content">
                            <span className="card-title">Book Search</span>
                            <br />
                            <form>
                                <div className="input-field">
                                    <input id="book" type="text" className="validate" onChange={this.handleChange}/>
                                    <label>Search here for books</label>
                                </div>
                                <button className="btn waves-effect waves-light" type="submit" name="action">Submit
                                <i className="material-icons right">send</i>
                                </button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
            // </div>
        );
    }
};

export default SearchCard;
